function h = p1(s, varargin)%P1  - sets xlim to length of plotted signal%%	usage:  h = p1(s, ...)%	  or    h = p1(x, s, ...)%% plots S within current axis, setting XLIM to [1 size(s,1)]% any additional arguments passed to PLOT%% if optional X units specified XLIM set to [x(1) x(end)]%% optionally returns line handle H%% for antialiasing:  set(h,'lineSmoothing','on');% mkt 11/97if nargin < 1,	eval('help p1');	return;end;if nargin>1 & isnumeric(varargin{1}),	x = s;	s = varargin{1};	varargin(1)=[];else,	if size(s,1) == 1, s = s(:); end;	x = [1 : size(s,1)];end;h = plot(x, squeeze(s), varargin{:});set(gca, 'xlim', [x(1) x(end)]);if nargout < 1, clear h; end;